definition.script:
pipeline{
    agent any
    stages{
        stage('stage-1'){
            steps{
                echo 'executing mvn clean'
                echo 'executing mvn package'
            }
        }

        stage('stage-2'){
            steps{
                echo 'building docker image'
            }
        }
        stage('stage-3'){
            steps{
                echo 'deploying docker image'
            }
        }
    }
    post{
        always{
            echo 'this will always execute'
        }
    }
}
